import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import WorldviewCard from '../components/worldview/WorldviewCard';
import './Home.css';

const Home = () => {
  const [worldviews, setWorldviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [category, setCategory] = useState('全部');
  const [search, setSearch] = useState('');

  useEffect(() => {
    const fetchWorldviews = async () => {
      try {
        setLoading(true);
        const params = new URLSearchParams({
          page: currentPage,
          limit: 16 // 改为每页16个，以适应4x4的网格布局
        });

        if (category !== '全部') {
          params.append('category', category);
        }

        if (search) {
          params.append('search', search);
        }

        const res = await fetch(`/api/worldviews?${params}`);
        const data = await res.json();

        setWorldviews(data.worldviews);
        setTotalPages(data.totalPages);
        setLoading(false);
      } catch (err) {
        console.error('获取世界观列表失败:', err);
        setLoading(false);
      }
    };

    fetchWorldviews();
  }, [currentPage, category, search]);

  useEffect(() => {
    // 从URL参数中获取搜索关键词
    const urlParams = new URLSearchParams(window.location.search);
    const searchParam = urlParams.get('search');
    if (searchParam) {
      setSearch(searchParam);
    }
  }, []);

  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo(0, 0);
  };

  return (
    <div className="home">
      <div className="hero">
        <div className="hero-content">
          <h1>探索无限的世界观</h1>
          <p>发现、创建和分享各种精彩的世界观设定</p>
          <Link to="/create-worldview" className="btn btn-primary btn-lg">
            创建你的世界观
          </Link>
        </div>
      </div>

      <div className="filters">
        <form className="search-form" onSubmit={(e) => {
          e.preventDefault();
          setCurrentPage(1);
          // 立即触发搜索以确保搜索执行
          const fetchWorldviews = async () => {
            try {
              setLoading(true);
              const params = new URLSearchParams({
                page: 1,
                limit: 16
              });

              if (category !== '全部') {
                params.append('category', category);
              }

              if (search) {
                params.append('search', search);
              }

              const res = await fetch(`/api/worldviews?${params}`);
              const data = await res.json();

              setWorldviews(data.worldviews);
              setTotalPages(data.totalPages);
              setLoading(false);
            } catch (err) {
              console.error('获取世界观列表失败:', err);
              setLoading(false);
            }
          };
          fetchWorldviews();
        }}>
          <input
            type="text"
            className="form-control"
            placeholder="搜索世界观..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <button type="submit" className="btn">搜索</button>
        </form>

        <div className="categories">
          <button 
            className={`category-btn ${category === '全部' ? 'active' : ''}`}
            onClick={() => {
              setCategory('全部');
              setCurrentPage(1);
            }}
          >
            全部
          </button>
          <button 
            className={`category-btn ${category === '奇幻' ? 'active' : ''}`}
            onClick={() => {
              setCategory('奇幻');
              setCurrentPage(1);
            }}
          >
            奇幻
          </button>
          <button 
            className={`category-btn ${category === '科幻' ? 'active' : ''}`}
            onClick={() => {
              setCategory('科幻');
              setCurrentPage(1);
            }}
          >
            科幻
          </button>
          <button 
            className={`category-btn ${category === '历史' ? 'active' : ''}`}
            onClick={() => {
              setCategory('历史');
              setCurrentPage(1);
            }}
          >
            历史
          </button>
          <button 
            className={`category-btn ${category === '现代' ? 'active' : ''}`}
            onClick={() => {
              setCategory('现代');
              setCurrentPage(1);
            }}
          >
            现代
          </button>
        </div>
      </div>

      <div className="worldviews-container">
        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
          </div>
        ) : worldviews && worldviews.length > 0 ? (
          <>
            <div className="worldviews-grid">
              {worldviews.map(worldview => (
                <WorldviewCard key={worldview.id} worldview={worldview} showNumber={true} />
              ))}
            </div>

            {totalPages > 1 && (
              <div className="pagination">
                <button
              className="pagination-btn"
              disabled={currentPage === 1}
              onClick={() => handlePageChange(currentPage - 1)}
            >
              上一页
            </button>

            <span className="page-info">
              第 {currentPage} 页，共 {totalPages} 页
            </span>

            <button
              className="pagination-btn"
              disabled={currentPage === totalPages}
              onClick={() => handlePageChange(currentPage + 1)}
            >
              下一页
            </button>
              </div>
            )}
          </>
        ) : (
          <div className="empty-state">
            <h3>没有找到世界观</h3>
            <p>试试调整搜索条件或创建一个新的世界观</p>
            <Link to="/create-worldview" className="btn">创建世界观</Link>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
