import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import WorldviewCard from '../components/worldview/WorldviewCard';
import './Home.css';

const Home = () => {
  const [worldviews, setWorldviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [category, setCategory] = useState('全部');
  const [search, setSearch] = useState('');

  useEffect(() => {
    const fetchWorldviews = async () => {
      try {
        setLoading(true);
        const params = new URLSearchParams({
          page: currentPage,
          limit: 16 // 改为每页16个，以适应4x4的网格布局
        });

        if (category !== '全部') {
          params.append('category', category);
        }

        if (search) {
          params.append('search', search);
        }

        const res = await fetch(`/api/worldviews?${params}`);
        const data = await res.json();

        setWorldviews(data.worldviews);
        setTotalPages(data.totalPages);
        setLoading(false);
      } catch (err) {
        console.error('获取世界观列表失败:', err);
        setLoading(false);
      }
    };

    fetchWorldviews();
  }, [currentPage, category, search]);

  useEffect(() => {
    // 从URL参数中获取搜索关键词
    const urlParams = new URLSearchParams(window.location.search);
    const searchParam = urlParams.get('search');
    if (searchParam) {
      setSearch(searchParam);
    }
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    setCurrentPage(1);
  };

  const handleCategoryChange = (newCategory) => {
    setCategory(newCategory);
    setCurrentPage(1);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo(0, 0);
  };

  return (
    <div className="home">
      <div className="hero">
        <h1>探索无限的世界观</h1>
        <p>发现、创建和分享各种精彩的世界观设定</p>
        <Link to="/create-worldview" className="btn btn-primary btn-lg">
          创建你的世界观
        </Link>
      </div>

      <div className="worldviews-container">
        {loading ? (
          <div className="text-center">加载中...</div>
        ) : worldviews && worldviews.length > 0 ? (
          <>
            <div className="worldviews-grid">
              {worldviews.map(worldview => (
                <WorldviewCard key={worldview.id} worldview={worldview} />
              ))}
            </div>

            {totalPages > 1 && (
              <div className="pagination">
                <button
                  className="btn"
                  disabled={currentPage === 1}
                  onClick={() => handlePageChange(currentPage - 1)}
                >
                  上一页
                </button>

                <span className="page-info">
                  第 {currentPage} 页，共 {totalPages} 页
                </span>

                <button
                  className="btn"
                  disabled={currentPage === totalPages}
                  onClick={() => handlePageChange(currentPage + 1)}
                >
                  下一页
                </button>
              </div>
            )}
          </>
        ) : (
          <div className="text-center">
            <h3>没有找到世界观</h3>
            <p>试试调整搜索条件或创建一个新的世界观</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
