import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import AvatarUpload from '../components/AvatarUpload';
import './Profile.css';

const Profile = () => {
  const { id } = useParams();
  const { user, isAuthenticated, updateUser } = useAuth();
  const [profileData, setProfileData] = useState({
    username: '',
    email: '',
    bio: '',
    avatar: ''
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [activeTab, setActiveTab] = useState('profile');
  const [loading, setLoading] = useState(false);
  const [passwordLoading, setPasswordLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [passwordMessage, setPasswordMessage] = useState('');
  const [userWorldviews, setUserWorldviews] = useState([]);
  const [userLikedWorldviews, setUserLikedWorldviews] = useState([]);

  useEffect(() => {
    if (isAuthenticated && user) {
      setProfileData({
        username: user.username || '',
        email: user.email || '',
        bio: user.bio || '',
        avatar: user.avatar || ''
      });

      fetchUserData();
    }
  }, [isAuthenticated, user]);

  const fetchUserData = async () => {
    try {
      // 获取用户的世界观
      const worldviewsRes = await fetch('/api/worldviews/user', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const worldviewsData = await worldviewsRes.json();

      if (worldviewsRes.ok) {
        setUserWorldviews(worldviewsData.worldviews);
      }

      // 获取用户点赞的世界观
      try {
        const likedRes = await fetch('/api/users/liked', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });

        if (likedRes.ok) {
          const likedData = await likedRes.json();
          setUserLikedWorldviews(likedData.likedWorldviews || []);
        } else {
          // 如果API端点不存在，设置为空数组
          setUserLikedWorldviews([]);
        }
      } catch (err) {
        // 如果请求失败，设置为空数组
        console.log('获取点赞的世界观失败，可能是功能未实现:', err);
        setUserLikedWorldviews([]);
      }
    } catch (err) {
      console.error('获取用户数据失败:', err);
    }
  };

  const handleProfileChange = (e) => {
    setProfileData({
      ...profileData,
      [e.target.name]: e.target.value
    });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({
      ...passwordData,
      [e.target.name]: e.target.value
    });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const res = await fetch('/api/users/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(profileData)
      });

      const data = await res.json();

      if (res.ok) {
        setMessage('个人资料更新成功！');
        updateUser(data.user);
      } else {
        setMessage(data.message || '更新失败，请重试');
      }
    } catch (err) {
      setMessage('更新失败，请重试');
      console.error('更新个人资料失败:', err);
    }

    setLoading(false);
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setPasswordMessage('新密码和确认密码不匹配');
      return;
    }

    setPasswordLoading(true);
    setPasswordMessage('');

    try {
      const res = await fetch('/api/users/password', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword
        })
      });

      const data = await res.json();

      if (res.ok) {
        setPasswordMessage('密码修改成功！');
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
      } else {
        setPasswordMessage(data.message || '密码修改失败，请重试');
      }
    } catch (err) {
      setPasswordMessage('密码修改失败，请重试');
      console.error('修改密码失败:', err);
    }

    setPasswordLoading(false);
  };

  // const formatDate = (dateString) => {
  //   const options = { 
  //     year: 'numeric', 
  //     month: 'short', 
  //     day: 'numeric'
  //   };
  //   return new Date(dateString).toLocaleDateString(undefined, options);
  // };

  // 如果有ID参数，则重定向到UserProfile页面
  if (id) {
    return (
      <div className="profile-page">
        <div className="container">
          <div className="text-center mt-5">正在跳转到用户资料页面...</div>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return (
      <div className="profile-page">
        <div className="container">
          <div className="login-prompt">
            <h2>请先登录</h2>
            <p>您需要登录才能查看个人资料页面</p>
            <a href="/login" className="btn btn-primary">去登录</a>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="container">
        <div className="profile-header">
          <div className="profile-avatar">
            <img 
              src={profileData.avatar || 'https://picsum.photos/seed/avatar/150/150.jpg'} 
              alt="用户头像" 
            />
          </div>
          <div className="profile-info">
            <h1>{profileData.username}</h1>
            <p className="profile-email">{profileData.email}</p>
            <p className="profile-id">用户ID: {user?.id}</p>
            {profileData.bio && <p className="profile-bio">{profileData.bio}</p>}
          </div>
        </div>

        <div className="profile-tabs">
          <button 
            className={`tab-btn ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            编辑资料
          </button>
          <button 
            className={`tab-btn ${activeTab === 'password' ? 'active' : ''}`}
            onClick={() => setActiveTab('password')}
          >
            修改密码
          </button>
          <button 
            className={`tab-btn ${activeTab === 'worldviews' ? 'active' : ''}`}
            onClick={() => setActiveTab('worldviews')}
          >
            我的世界观
          </button>
          <button 
            className={`tab-btn ${activeTab === 'liked' ? 'active' : ''}`}
            onClick={() => setActiveTab('liked')}
          >
            点赞的世界观
          </button>
        </div>

        <div className="profile-content">
          {activeTab === 'profile' && (
            <div className="profile-form-container">
              <h2>编辑个人资料</h2>
              {message && <div className="alert alert-info">{message}</div>}

              <form onSubmit={handleProfileSubmit} className="profile-form">
                <div className="form-group">
                  <label htmlFor="username">用户名</label>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    value={profileData.username}
                    onChange={handleProfileChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="email">邮箱</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={profileData.email}
                    onChange={handleProfileChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="bio">个人简介</label>
                  <textarea
                    id="bio"
                    name="bio"
                    value={profileData.bio}
                    onChange={handleProfileChange}
                    className="form-control"
                    rows="4"
                    placeholder="介绍一下你自己..."
                  ></textarea>
                </div>

                <div className="form-group">
                  <label htmlFor="avatar">头像URL</label>
                  <input
                    type="text"
                    id="avatar"
                    name="avatar"
                    value={profileData.avatar}
                    onChange={handleProfileChange}
                    className="form-control"
                    placeholder="https://example.com/avatar.jpg"
                  />
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={loading}
                >
                  {loading ? '保存中...' : '保存更改'}
                </button>
              </form>
            </div>
          )}

          {activeTab === 'password' && (
            <div className="password-form-container">
              <h2>修改密码</h2>
              {passwordMessage && <div className="alert alert-info">{passwordMessage}</div>}

              <form onSubmit={handlePasswordSubmit} className="password-form">
                <div className="form-group">
                  <label htmlFor="currentPassword">当前密码</label>
                  <input
                    type="password"
                    id="currentPassword"
                    name="currentPassword"
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                    className="form-control"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="newPassword">新密码</label>
                  <input
                    type="password"
                    id="newPassword"
                    name="newPassword"
                    value={passwordData.newPassword}
                    onChange={handlePasswordChange}
                    className="form-control"
                    required
                    minLength="6"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="confirmPassword">确认新密码</label>
                  <input
                    type="password"
                    id="confirmPassword"
                    name="confirmPassword"
                    value={passwordData.confirmPassword}
                    onChange={handlePasswordChange}
                    className="form-control"
                    required
                    minLength="6"
                  />
                </div>

                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={passwordLoading}
                >
                  {passwordLoading ? '修改中...' : '修改密码'}
                </button>
              </form>
            </div>
          )}

          {activeTab === 'worldviews' && (
            <div className="user-worldviews">
              <h2>我的世界观</h2>
              {userWorldviews.length > 0 ? (
                <div className="worldviews-grid">
                  {userWorldviews.map(worldview => (
                    <div key={worldview.id} className="worldview-card">
                      <div className="worldview-image">
                        <img 
                          src={worldview.coverImage || 'https://picsum.photos/seed/worldview/300/200.jpg'} 
                          alt={worldview.title} 
                        />
                      </div>
                      <div className="worldview-content">
                        <h3>{worldview.title}</h3>
                        <div className="worldview-number">编号: #{worldview.worldviewNumber}</div>
                        <p>{worldview.description}</p>
                        <div className="worldview-meta">
                          <span>浏览: {worldview.views}</span>
                          <span>点赞: {worldview.likes?.length || 0}</span>
                          <span>评论: {worldview.comments?.length || 0}</span>
                        </div>
                        <div className="worldview-actions">
                          <a href={`/worldview/${worldview.id}`} className="btn btn-sm btn-outline">查看</a>
                        <a href={`/edit-worldview/${worldview.id}`} className="btn btn-sm btn-primary">编辑</a>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="empty-state">
                  <p>您还没有创建任何世界观</p>
                  <a href="/create-worldview" className="btn btn-primary">创建世界观</a>
                </div>
              )}
            </div>
          )}

          {activeTab === 'liked' && (
            <div className="user-liked-worldviews">
              <h2>点赞的世界观</h2>
              {userLikedWorldviews.length > 0 ? (
                <div className="worldviews-grid">
                  {userLikedWorldviews.map(worldview => (
                    <div key={worldview.id} className="worldview-card">
                      <div className="worldview-image">
                        <img 
                          src={worldview.coverImage || 'https://picsum.photos/seed/worldview/300/200.jpg'} 
                          alt={worldview.title} 
                        />
                      </div>
                      <div className="worldview-content">
                        <h3>{worldview.title}</h3>
                        <p>作者: {worldview.author?.username || '未知'}</p>
                        <div className="worldview-meta">
                          <span>浏览: {worldview.views || 0}</span>
                          <span>点赞: {worldview.likes?.length || 0}</span>
                          <span>评论: {worldview.comments?.length || 0}</span>
                        </div>
                        <div className="worldview-actions">
                          <a href={`/worldview/${worldview.id}`} className="btn btn-sm btn-primary">查看</a>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="empty-state">
                  <p>您还没有点赞任何世界观</p>
                  <a href="/" className="btn btn-primary">浏览世界观</a>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
